$(document).ready(function(){
    $('#navigation').fadeIn(250);
    $('#contentWeb').fadeIn(500);
    $('#logout_users').fadeIn(500);
    var before = "";
    $('#judulProdak').keydown(function(){
        before = $('#judulProdak').val();
    });
    $('#judulProdak').keyup(function(){
        if($('#judulProdak').val().length > 19){
            $('#judulProdak').val(before);
        }
    });
    
});

function formChooser(e){
    var restData;
    var dataHelper;
    var restStrings = '';
    var receiveIMG = $(e).prop('src');
    
    restData = receiveIMG.length - 7;
    for(dataHelper = restData;dataHelper<receiveIMG.length;dataHelper++)
        restStrings += receiveIMG[dataHelper];
    $('#chooser').val(restStrings);
}

function popUp(args){
    $(args).fadeIn();   
}
function popDown(args){
    $(args).fadeOut();   

}
